package org.miller.backend.security.Auth;import org.miller.backend.security.Payload.JwtResponse;import org.miller.backend.security.Payload.LoginRequest;import org.miller.backend.security.Payload.RegisterRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Map;@RestController@RequestMapping("/api/auth")public class AuthController {    @Autowired    AuthService authService;    @PostMapping("/login")    public ResponseEntity<JwtResponse> login(@RequestBody LoginRequest request){        return ResponseEntity.ok(authService.login(request));    }    @PostMapping("/register")    public ResponseEntity<?> register(@RequestBody RegisterRequest request){        try {            JwtResponse response = authService.register(request);            return ResponseEntity.ok(Map.of("success", true, "data", response));        } catch (IllegalArgumentException e) {            return ResponseEntity.badRequest().body(Map.of("success", false, "message", "Invalid input: " + e.getMessage()));        } catch (Exception e) {            return ResponseEntity.status(500).body(Map.of("success", false, "message", "An error occurred: " + e.getMessage()));        }    }}