package org.miller.backend.service;import org.miller.backend.model.Subtask;import java.util.List;import java.util.Optional;import java.util.UUID;public interface SubtaskService {    List<Subtask> getAllSubtasks();    Optional<Subtask> getSubtaskById(UUID id);    Subtask createSubtask(Subtask subtask);    Subtask updateSubtask(UUID id, Subtask subtaskDetails);    void deleteSubtask(UUID id);    List<Subtask> getSubtasksByParentTaskId(UUID parentTaskId);    List<Subtask> getSubtasksByAssignedTo(UUID assignedToUserId);}