package org.miller.backend.service;import org.miller.backend.model.User;import java.util.List;import java.util.Optional;import java.util.UUID;public interface UserService {    List<User> getAllUsers();    Optional<User> getUserById(UUID id);    User createUser(User user);    User updateUser(UUID id, User userDetails);    void deleteUser(UUID id);    Optional<User> getUserByUsername(String username);    Optional<User> getUserByEmail(String email);}